    // This function will classify the data using the saved Weka classifier
    // Input: the path for the specific classifier we're using, and that of 
    // the arff we created
    // Output: None, but the values for the classifiers will be filled
    // for that specific model
    public void findClassTotals(String model, String inputFile)
            throws Exception {

        // Get the Model from the path
        ObjectInputStream ois = new ObjectInputStream(
                new FileInputStream(model));
        Classifier cls = (Classifier) ois.readObject();
        ois.close();

        // load unlabeled data from the arff we created
        FileReader fr = new FileReader(inputFile);
        Instances unlabeled = new Instances(
                new BufferedReader(
                fr));


        // set class attribute (it's the last attribute listed in the file)
        unlabeled.setClassIndex(unlabeled.numAttributes() - 1);

        // create copy
        Instances labeled = new Instances(unlabeled);

        // label instances, and get the number of "yes" instances
        int numYes = 0;

        // Do the first classification outside the loop, so we can
        // specify the label of the first post in the thread

        // Label the data
        double clsLabel = cls.classifyInstance(unlabeled.instance(0));
        labeled.instance(0).setClassValue(clsLabel);

        // Get the label
        String label = labeled.instance(0).stringValue(
                labeled.instance(0).classAttribute());

        // if it's yes, increase number of yes's, 
        // and put "true" for the first post
        if (label.equals("yes")) {
            numYes++;
            if (model.contains("seeksInfo.model")) {
                firstPostSeeksInfo = true;
            } else {
                firstPostIsExperiential = true;
            }
        } // otherwise, put "false" for the first post, as it isn't in the class
        else {
            if (model.contains("seeksInfo.model")) {
                firstPostSeeksInfo = false;
            } else {
                firstPostIsExperiential = false;
            }
        }

        // repeat the process with the rest of the posts
        for (int i = 1; i < unlabeled.numInstances(); i++) {
            // label the data
            clsLabel = cls.classifyInstance(unlabeled.instance(i));
            labeled.instance(i).setClassValue(clsLabel);

            // get the label as a string
            label = labeled.instance(i).stringValue(
                    labeled.instance(i).classAttribute());

            // If the post fits the class, increase number of "yes" posts
            if (label.equals("yes")) {
                numYes++;
            }
        }
        if (model.contains("seeksInfo.model")) {
            percentInfoSeekingPosts = numYes / (double) numPostsInThread;
        } else {
            percentExperientialPosts = numYes / (double) numPostsInThread;
        }

        // Delete the arff file (we're done with it)
        fr.close();
        File file = new File(inputFile);
        file.delete();
    }

    // this function deals with the more complicated logical expressions;
    // it deals with "AND"'s, which sometimes have nested "OR"'s
    // input: the post content being matched, and the string of "and" inputs 
    // that need to be matched (either OR statements or just attributes)
    // all attributes in the statement must be regular expressions
    // output: a boolean of whether the "and" statement is true
    public boolean parseAnd(String content, String[] attribute) {
        // loop through the attribute string and see if it matches
        for (int i = 0; i < attribute.length; i++) {
            // if the attribute is an "or," we don't need everything
            // within the "or" statement to match, but only 1 thing.
            // loop through the statement until the endor and see if
            // at least one thing matches
            if (attribute[i].equals("OR")) {
                // keeps track of whether an attribute has been true
                boolean oneTrue = false;
                for (int j = i + 1; j < attribute.length; j++) {
                    // if we've reached the end of the "or" statement:
                    if (attribute[j].equals("ENDOR")) {
                        // if the "or" was false (no attribute was ever seen),
                        // the overarching statement is false; return false
                        if (!oneTrue) {
                            return false;
                        }
                        // otherwise, i needs to jump to one after the "ENDOR,"
                        // so the "and" statement doesn't start counting things
                        // within the "or" as being within "and"'s
                        i = j;
                        break;
                    }
                    // if anything matches, oneTrue is true;
                    // however, we need to continue the loop because 
                    // we need to know by how far i needs to jump
                    if (content.matches(attribute[j])) {
                        oneTrue = true;
                    }
                }
            } // if the attribute isn't an "or", just match the attribute;
            // if it's false, the overarching "and" is false too
            else {
                if (!(content.matches(attribute[i]))) {
                    return false;
                }
            }
        }
        // return true if we get through everything without returning false
        return true;

    }

    // This function creates a single arff file, based upon the posts and the 
    // integer corresponding to which of the classifiers is being used
    // Input: a number corresponding to the classifier being used
    // (citesInfoSource, isExperiential, etc.)
    // Output: the String of the file name
    public String createArffFile(int classNum) throws Exception {
        // the arff name will be based on the id and the classifier number
        String fileName = rootPath + "Files" + File.separator + Integer.toString(id)
                + Integer.toString(classNum) + new Random().nextInt(9999999)
                + ".arff";

        // First create a filewriter using the unique file name
        FileWriter fw = new FileWriter(fileName);
        BufferedWriter writer = new BufferedWriter(fw);

        // all ARFF files will start with this
        writer.write("@relation postFeatures\n\n");

        // the lines that correspond to the attributes themselves is 
        // created in a String in the Attributes.java class
        for (int i = 0; i < allAttributes[classNum].length; i++) {
            writer.write("@attribute " + allAttributes[classNum][i].getName()
                    + " " + allAttributes[classNum][i].getOptions() + "\n");
        }

        // The class for all of our classifiers is a simple yes/no binary
        writer.write("@attribute CLASS {no,yes}\n\n@data\n");
        // Each post will have the values of the attributes, comma-separated, 
        // in a line; ex of a line: 1,5,6,7,true,false,true,true...,?
        for (int i = 0; i < posts.length; i++) {
            // get the numeric attributes for this specific classifier
            Attribute[] numAttrs = allAttributes[classNum];
            // get their value and put it into the arff file
            for (int j = 0; j < numAttrs.length; j++) {
                // Get the specific attribute and type 
                String[] attribute = numAttrs[j].getAttr();
                String type = numAttrs[j].getType();
                String value = "";

                // If it's a string type, just see if the post 
                // contains the substring
                if (type.equals("string")) {
                    writer.write(String.valueOf(posts[i].
                            getContent().contains(attribute[0])));
                } //if it's a stem, we need to match a regular expression
                else if (type.equals("stem")) {
                    writer.write(String.valueOf(posts[i].
                            getContent().matches(attribute[0])));
                } // if it's an "and" call function that parses them
                else if (type.equals("and")) {
                    writer.write(String.valueOf(parseAnd(
                            posts[i].getContent(), attribute)));
                } // as "or's" are not nested (at least in what we've dealt
                // with so far), just make sure at least one term is there
                // all attributes within an "or" must be regular expressions
                else if (type.equals("or")) {
                    int oneThere = 0;
                    String content = posts[i].getContent();
                    for (int k = 0; k < attribute.length; k++) {
                        // if there's a term it has,
                        // write true, indicate that a term is
                        // there, and break out of the loop (as we
                        // can conclude the "or" statement is true)
                        if (content.matches(attribute[k])) {
                            oneThere = 1;
                            writer.write("true");
                            break;
                        }
                    }
                    // if it contains none of the terms, write "false;"
                    // the "or" statement is false
                    if (oneThere == 0) {
                        writer.write("false");
                    }
                } // if it's one of the numeric types, get the value 
                // for that type
                else if (attribute[0].equals("numLinks")) {
                    value = posts[i].getNumLinks();
                } else if (attribute[0].equals("numPostsByUser")) {
                    value = posts[i].numPostsByUser();
                } else if (attribute[0].equals("replyNum")) {
                    value = posts[i].replyNum();
                } else if (attribute[0].equals("isTerminating")) {
                    value = posts[i].isTerminating();
                } else if (attribute[0].equals("numChars")) {
                    value = posts[i].numChars();
                } else if (attribute[0].equals("wordsPerPara")) {
                    value = posts[i].wordsParPara();
                } else if (attribute[0].equals("breaksToWordsRatio")) {
                    value = posts[i].breaksToWordsRatio();
                } else if (attribute[0].equals("numPostsInThread")) {
                    value = Integer.toString(posts.length);
                }

                if (value != null) {
                    writer.write(value);
                }

                // if it's the last attribute, put a question mark denoting the
                // unknown status of the class, and put a newline character
                if (j == numAttrs.length - 1) {
                    writer.write(",?\n");
                } // otherwise separate by comma
                else {
                    writer.write(",");
                }
            }
        }
        writer.close();
        // return the file string
        return fileName;
    }

    // Gets rid of all the quoted other posts within posts, because those
    // confound the results
    // Input: the entire thread (raw html)
    // Output: the same, minus any quoted posts within posts
    public String cutBlockQuotes(String thread) {
        // Find the index of the first blockquote
        int openIndex = thread.indexOf("<blockquote>");

        // while there exists a blockquote, loop
        while (openIndex != -1) {
            // cut the blockquote out
            thread = thread.substring(0, openIndex)
                    + thread.substring(thread.indexOf("</blockquote>") + 13);
            // find the next blockquote
            openIndex = thread.indexOf("<blockquote>");
        }
        return thread;
    }

    // Strips the HTML code so our number of characters is correct
    // Does NOT strip <br/> tags so we can get the average words per para
    // Input: the post content, including all HTML
    // Output: the post content, without any HTML tags besides <br/> tags
    // Reminder: run AFTER the countLinks function!
    public String stripHTML(String post) {
        // Find the index of the first tag
        int openIndex = post.indexOf("<");

        // what we'll be returning
        String strippedContent = "";

        // while there exists an open tag in the post, loop
        while (openIndex != -1) {
            // append everything up to the first tag into the string to return
            strippedContent += post.substring(0, openIndex);
            if (post.substring(openIndex, openIndex + 6).equals("<br />")) {
                strippedContent += "<br/>";
            }
            // cut the tag out of post
            post = post.substring(post.indexOf(">") + 1);
            // find the next blockquote
            openIndex = post.indexOf("<");
        }
        // get the end of the string
        strippedContent += post;
        return strippedContent;
    }

    // this function takes the post content and counts the number of links
    // Input: the post (BEFORE HTML IS STRIPPED)
    // Output: the number of links in the post
    // TO DO: make it "number of links that are informative links!"
    public String countNumLinks(String post) {
        // Find the index of the first 'a' tag
        int openIndex = post.indexOf("<a href");
        int numInfoLinks = 0;
        String url = "";
        // while there exists an 'a' tag, loop
        while (openIndex != -1) {
            // truncate the post to that point
            post = post.substring(openIndex + 4);

            // the url is between the closing carrot of this a tag and the open
            // carrot of the reciprocal closing a tag
            url = post.substring(post.indexOf('>') + 1, post.indexOf('<'));

            // Match to url's we care about
            for (int i = 0; i < infoLinks.length; i++) {
                if (url.contains(infoLinks[i])) {
                    // if the host of the URL matches a host we care about,
                    // increase the number of informative links
                    // (once it matches one of the links we can break the loop)
                    numInfoLinks++;
                    break;
                }
            }

            // find the next 'a'
            openIndex = post.indexOf("<a href");
        }
        // return the number of links that fit the definition
        return Integer.toString(numInfoLinks);
    }

    // this function takes the post content and finds the number 
    // of br tags
    // Input: the post content
    // Output: the number of paragraph tags
    public int countNumBrs(String post) {
        // Find the index of the first 'br' tag
        int openIndex = post.indexOf("<br/>");
        int numBrs = 0;
        // while there exists an 'a' tag, loop
        while (openIndex != -1) {
            numBrs++;
            // cut the 'br' out
            post = post.substring(openIndex + 3);
            // find the next 'br'
            openIndex = post.indexOf("<br/>");
        }
        return numBrs;
    }

    // This function takes the raw HTML from the thread and processes 
    // it into different posts
    // Input: the entire HTML string grabbed from the URL
    // Output: NONE
    // Result: an array of post objects (includes all possible attributes)
    public void processThread(String thread) {
        // split the thread at a point just before the post
        String[] postAreas;

        // make sure the page isn't a "you have requested a topic that 
        // doesn't exist" page before proceeding
        if (thread.contains("reportpost.gif")) {
            postAreas = thread.split("reportpost.gif");
        } else {
            return;
        }

        // declare the posts array
        posts = new Post[postAreas.length - 1];
        for (int i = 0; i < posts.length; i++) {

            // declare the new post
            posts[i] = new Post();

            // parse the post out of the string.
            String[] postData = postAreas[i].
                    split("<hr size=\"1\" style=\"color:#a8a8a8\" />");
            String post = postData[1];
            // since they're weird with their html tags,
            // I just set everything to lower case
            post = post.toLowerCase();
            posts[i].setNumLinks(countNumLinks(post));
            post = stripHTML(post);
            posts[i].setContent(post);
            int numBreaks = countNumBrs(post);
            // Just get the length of the string for the number of characters 
            posts[i].setnumChars(Integer.toString(post.length()));

            // the number of breaks divided by the number of total words
            posts[i].setbreaksToWordsRatio(Double.toString(
                    numBreaks / (double) (post.split("\\s+").length)));

            // find the average paragraph length by splitting at the paragraph
            // tag, getting the length of each paragraph, then taking the avrg.
            int parLength = 0;
            String[] contentSplitAtParas = post.split("<br/>");
            int length = contentSplitAtParas.length;
            for (int j = 0; j < length; j++) {
                parLength += contentSplitAtParas[j].length();
            }
            posts[i].setwordsParPara(Double.toString(
                    parLength / (double) length));

            // This ugly splitting gets the number of posts by the user.
            // First, take everything after the second "hr" tag;
            // that's where all the user information is.  Then split at
            // the "Posts: <b>" substring; we want everything after that.  
            // Finally, since the number of posts is always followed by
            // a </b>, split at that, and take everything before it.
            if (postData[2].split("Posts: <b>").length > 1) {
                String numPosts = postData[2].split("Posts: <b>")[1].
                        split("</b>")[0];
                if (!(numPosts.equals(""))) {
                    posts[i].setnumPostsByUser(numPosts);
                } else {
                    posts[i].setnumPostsByUser("0");
                }
            } else {
                posts[i].setnumPostsByUser("0");
            }


            // the reply num is just whatever i is; if i=0, it's the
            // first post, if i is one it's the second, and so on
            posts[i].setreplyNum(Integer.toString(i));

            // if it's the last post in the thread, it's terminating; 
            // otherwise it isn't
            if (i == postAreas.length - 2) {
                posts[i].setisTerminating("1");
            } else {
                posts[i].setisTerminating("0");
            }
        }

    }

