    // This function orchestrates the entire classification system.
    // It's the one Cheng's function will call
    // Input: a string with the raw HTML
    // Output: None, but by the end all of the fields will be filled
    public String findAllClasses(String threadContent) throws Exception {
        // increase the ID so there's a new one
        id++;

        // if the function hasn't been called before, fill the arrays
        if (allAttributes == null) {
            fillArrays();
        }

        // fill the posts array and get the number of posts in the thread
        processThread(cutBlockQuotes(threadContent));

        // if the posts array is null the page had no posts;
        // just null out all the values and return
        if (posts == null) {
            percentInfoSeekingPosts = 0;
            percentExperientialPosts = 0;
            percentPostsWithInfoLinks = 0;
            percentPostsWrittenByProlific = 0;
            numPostsInThread = 0;
            numInfoLinksInThread = 0;
            firstPostSeeksInfo = false;
            firstPostIsExperiential = false;
            firstPostContainsInfoLink = false;
            firstPostWrittenByProlific = false;
            threadNotFound = true;
//            return "numPost@" + Integer.toString(numPostsInThread)
//                    //                    + "@seekInfo" + Double.toString(percentInfoSeekingPosts)
//                    + "*experiential" + Double.toString(percentExperientialPosts)
//                    + "%infoLinks" + Double.toString(percentPostsWithInfoLinks) + "#";
////                + "#prolific" + Double.toString(percentPostsWrittenByProlific);
            return "{" + "\"numPost\":" + '"' + Integer.toString(numPostsInThread) + '"' + ",\"experiential\":" + '"' + Double.toString(percentExperientialPosts) + '"' + ",\"infoLinks\":" + '"' + Double.toString(percentPostsWithInfoLinks) + '"'
                    + "}";
        }

        threadNotFound = false;

        numPostsInThread = posts.length;


        // Classify the posts using the classifiers
        for (int i = 0; i < modelLocations.length; i++) {
            findClassTotals(modelLocations[i], createArffFile(i));
        }

        // number of info-based links out in the thread
        int totalLinks = 0;

        // number of posts in the thread with info-based links out
        int numPostsWithLinks = 0;
        int infoLinksInPost;

        // number of posts by authors who have posted more than 500 posts
        int numPostsWithProlificUsers = 0;

        // Whether the first post in the thread is written by an author who has
        // posted more than 500 posts
        if (Integer.parseInt(posts[0].numPostsByUser()) > 500) {
            firstPostWrittenByProlific = true;
            numPostsWithProlificUsers++;
        }

        // whether the first post in the thread has >0 info-based links
        int numLinksInFirst = Integer.parseInt(posts[0].getNumLinks());
        if (numLinksInFirst > 0) {
            firstPostContainsInfoLink = true;
            totalLinks += numLinksInFirst;
        }

        // for all the posts in the thread
        for (int i = 1; i < posts.length; i++) {
            // get the number of informative links in the post
            infoLinksInPost = Integer.parseInt(posts[i].getNumLinks());
            if (infoLinksInPost > 0) {
                totalLinks += infoLinksInPost;
                numPostsWithLinks++;
            }
            // see if the author has posted more than 500 posts
            if (Integer.parseInt(posts[i].numPostsByUser()) > 500) {
                numPostsWithProlificUsers++;
            }
        }

        // percentage of posts whose authors are prolific
        percentPostsWrittenByProlific = numPostsWithProlificUsers / (double) posts.length;

        //percentage of posts with links out
        percentPostsWithInfoLinks = numPostsWithLinks / (double) posts.length;


        // total number of links in the thread
        numInfoLinksInThread = totalLinks;



        // FOR DEBUGGING
        System.out.println("Num posts in thread: " + numPostsInThread);
        System.out.println("Number of Info Links in Thread: "
                + numInfoLinksInThread);
        System.out.println("First post seeks info? " + firstPostSeeksInfo);
        System.out.println("First post is Experiential? "
                + firstPostIsExperiential);
        System.out.println("First post has info link? "
                + firstPostContainsInfoLink);
        System.out.println("First post written by prolific? "
                + firstPostWrittenByProlific);
        System.out.println("Percent of posts seek info: "
                + percentInfoSeekingPosts);
        System.out.println("Percent of posts are experiential: "
                + percentExperientialPosts);
        System.out.println("Percent of posts have info links: "
                + percentPostsWithInfoLinks);
        System.out.println("Percent of posts written by prolific: "
                + percentPostsWrittenByProlific + "\n");

//        return "numPost@" + Integer.toString(numPostsInThread)
//                //                + "@seekInfo" + Double.toString(percentInfoSeekingPosts)
//                + "*experiential" + Double.toString(percentExperientialPosts)
//                + "%infoLinks" + Double.toString(percentPostsWithInfoLinks) + "#";
////                + "#prolific" + Double.toString(percentPostsWrittenByProlific);
        return "{" + "\"numPost\":" + '"' + Integer.toString(numPostsInThread) + '"' + ",\"experiential\":" + '"' + Double.toString(percentExperientialPosts) + '"' + ",\"infoLinks\":" + '"' + Double.toString(percentPostsWithInfoLinks) + '"'
                + "}";

    }

